import{createApp}from"vue";import test from"./components/carousel.vue";import modalController from"./components/modal-controller.vue";var app=createApp(test);app.mount("#app");var app2=createApp(test);app2.mount("#app-second");var app3=createApp(test);app3.mount("#app-third");var modals=createApp(modalController);modals.mount("#modals-controller");var burger=document.querySelector(".burger"),background=document.querySelector(".focusbackground"),menu=document.querySelector(".lg-menu"),loginForm=document.querySelector(".login-block"),loginButton=document.querySelector(".js-login");function disableBodyScroll(){document.querySelector("body").classList.add("stop-scroll")}function enableBodyScroll(){document.querySelector("body").classList.remove("stop-scroll")}function toggleMenu(){background.classList.add("active"),menu.classList.add("active"),disableBodyScroll()}function closeAll(){background.classList.remove("active"),menu.classList.remove("active"),loginForm.classList.remove("active"),enableBodyScroll()}function openLoginForm(){background.classList.add("active"),loginForm.classList.add("active"),disableBodyScroll()}window.addEventListener("load",(function(){burger.addEventListener("click",toggleMenu),background.addEventListener("click",closeAll),loginButton.addEventListener("click",openLoginForm)}));